svg.script:

const showDuration = 2000; // ms per line
const fadeDuration = 400;  // ms fade transition (not used here for simplicity)
let current = 1;
function showLine(n) {
  for (let i = 1; i <= 7; i++) {
    const el = document.getElementById('t' + i);
    if (el) el.setAttribute('class', 'text' + (i === n ? ' show' : ' hide'));
  }
}
function animate() {
  showLine(current);
  if (current < 7) {
    setTimeout(() => {
      current += 1;
      animate();
    }, showDuration);
  }
}
animate();
